mapper.select:
select * from company
		where pubtime is not null

		AND trade = #{trade} 


		AND salary = #{salary} 


		AND to_char(pubtime,'YYYY-MM-DD HH24:MI:SS') < #{pubtime}

mapper.update:
update company set name=#{name},location=#{location},
phone=#{phone},email=#{email},trade=#{trade},salary=#{salary},tip=#{tip}
where id=#{id}
